import React from "react";
import { useState } from "react";

const List= () => {
   const [tarefa, setTarefa] = useState("");
   const [itemsList, setItemsList] = useState([]);
   const capturarInput = (e) => {
        const inputTarefa = e.target.value;
        setTarefa(inputTarefa);
    }
   const addItemsList = (e) => {
        e.preventDefault(); 
        setItemsList([...itemsList, tarefa]); 
        setTarefa("");
    }
   return(
        <div className="bg-green-800 w-screen h-screen flex justify-center items-center flex-col text-green-200">
            <form onSubmit={addItemsList} className="">
                <input type="text" placeholder="Adicione uma tarefa" onChange={capturarInput} value={tarefa} className="w-[500px] h-[35px] rounded-xl m-7  text-gray-500 outline-none shadow-md "/>
                <button type="submit">ADD</button>
            </form>

            <ul className="todo-list" >
                {itemsList.map((item, index) => ( 
  	            <li key={index}>{item}</li> 
	            ))}
            </ul>
       </div>
   )
}
export default List;
