import React from "react";
import { useState } from "react";

const List= () => {
   const [tarefa, setTarefa] = useState("");
   const [itemsList, setItemsList] = useState([]);
   const capturarInput = (e) => {
        const inputTarefa = e.target.value;
        setTarefa(inputTarefa);
    }
   const addItemsList = (e) => {
        e.preventDefault(); 
        setItemsList([...itemsList, tarefa]); 
        setTarefa("");
    }
   return(
        <div className="bg-green-200 w-screen h-screen justify-center justify-items-center flex flex-col">
            <h1>ToDo List</h1>
            <form onSubmit={addItemsList}>
                <input type="text" placeholder="Adicione uma tarefa" onChange={capturarInput} value={tarefa} />
                <button type="submit">Adicionar</button>
            </form>

            <ul className="todo-list" >
                {itemsList.map((item, index) => ( 
  	            <li key={index}>{item}</li> 
	            ))}
            </ul>
       </div>
   )
}
export default List;
